mapper.select:
SELECT id, title, content, author, views, created_at as createdAt, updated_at as updatedAt
FROM notices
WHERE id = #{id}

mapper.update:
UPDATE notices
SET title = #{title}, content = #{content}, author = #{author}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}

UPDATE notices
SET views = views + 1, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
